<!-- <input type="checkbox" #chb (change)="showStudentByDone(chb.checked)"/>show active students<br/> -->
<ul class="list-group">
    <li *ngFor=" let i of courses" class="list-group-item" [ngClass]="{'list-group-item-danger': i.date}">
        <div class="card">
            <div class="col-1">
                <img src="{{ i.image }}" class="card-img-top" [ngStyle]="{ 'width.px': 50, 'height.px': 35 }" />  name: {{i.name}}
            </div>
            <div class="card-body">
                <h5 class="card-title">Card title</h5>
                <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>

              </div>
            <div class="col-2">
            </div>
        </div>  
    </li>
</ul>
