import { Component } from '@angular/core';
import { Course } from '../course.model';
import { CourseService } from '../course.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-all-courses',
  templateUrl: './all-courses.component.html',
  styleUrls: ['./all-courses.component.scss']
})
export class AllCoursesComponent {
  constructor(private _courseService: CourseService,private _router:Router) {
    _courseService.getCursesFromServer().subscribe(data => {
      this.courses = data;
    })

  }
 courses:Course[];

}
<p>all-courses works!</p>
<input type="text" placeholder="enter name of course" [(ngModel)]="cName" (input)="getStudentByName()" />
<input type="checkbox" #chb (change)="showStudentByDone(chb.checked)"/>show active students<br/>
<ul class="list-group">
    <li *ngFor=" let i of students" class="list-group-item" [ngClass]="{'list-group-item-danger': i.active,'list-group-item-success':i.avgMarks>90}">
        <div class="row">
            <div class="col-6">id: {{i.id}}, last name: {{i.name}}, famalyName: {{i.famalyName}}
            </div>

            <div class="col-2">
                <button  class="btn btn-secondary" (click)="deleteStudent(i)">delete Student</button>
            </div>
            <div class="col-2">
                <button  class="btn btn-secondary" (click)="showDetails2(i)">show Details</button>
            </div>
            <div class="col-2">
                <button class="btn btn-secondary" (click)="showDetails(i)">updete details</button>
            </div>
        </div>  
    </li>
</ul>
<br>
