import { Component, EventEmitter, Input, Output } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { LoginService } from '../user.service';
import { User } from '../user.model';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent {
  user:User();
  loginForm: FormGroup= new FormGroup({});
  :FormGroup=new FormGroup({
    "userName":new FormControl("",[Validators.required,Validators.minLength(3)]),
    "password":new FormControl("",[Validators.required,Validators.minLength(3)]),
  })
  constructor(private _usrService:LoginService){}
  login(){
    this._usrService.userId=this.loginForm.controls["userName"].value;
  }

}
<p>login works!</p>
<div>
    <form [formGroup]="loginForm">
        <div class="form-group">
            <label>שם משתמש</label>
            <input type="text" formControlName="userName" [value]="" >
            <ng-container *ngIf="loginForm.controls['userName'].errors&& !loginForm.controls['userName']" >
                <div class="alert alert-danger"> not name valid</div>
            </ng-container>
        </div>
        <div class="form-group">
            <label>סיסמה</label>
            <input type="password" formControlName="password">
            <ng-container *ngIf="loginForm.controls['password'].errors&& !loginForm.controls['password']" >
                <div class="alert alert-danger"> not name valid</div>
            </ng-container>
        </div>
        <button class="save" [disabled]="!loginForm.valid" (click)="login()">שלח</button>
    </form>


</div>
