import { Component, EventEmitter, Input, Output } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { UserService } from '../user.service';
import { User } from '../user.model';
@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.scss']
})
export class RegisterComponent {

  u:User;
  registerForm: FormGroup= new FormGroup({});
  constructor(private _usrService:UserService){
    this.registerForm=new FormGroup({
      "userName":new FormControl(this.u?.name,[Validators.required,Validators.minLength(3)]),
      "password":new FormControl(this.u?.password,[Validators.required,Validators.minLength(3)]),
      "address":new FormControl("",[Validators.required,Validators.minLength(3)]),
      "email":new FormControl("",[Validators.required,Validators.minLength(3)]),
    })
  }
  register()
  {
    this.u.name=this.registerForm.controls["userName"].value;
    this.u.password=this.registerForm.controls["password"].value;
    this.u.address=this.registerForm.controls["address"].value;
    this.u.email=this.registerForm.controls["email"].value;

    this._usrService.register(this.u).subscribe({
      next:(res=>{
        alert(res);
      })})

  }



}
<p>register works!</p>
<div>
    <form [formGroup]="registerForm">
        <div class="form-group">
            <label for="name">שם משתמש</label>
            <input type="text" class="form-control" formControlName="userName" [value]="">
            <ng-container *ngIf="registerForm.controls['userName'].errors&& !registerForm.controls['userName'].pristine">
                <div class="alert alert-danger"> not name valid</div>
            </ng-container>
        </div>
        <div class="form-group">
            <label>כתובת</label>
            <input type="text" class="form-control" formControlName="address">
            <ng-container *ngIf="registerForm.controls['address'].errors&& !registerForm.controls['address'].pristine">
                <div class="alert alert-danger"> not address valid</div>
            </ng-container>
        </div>
        <div class="form-group">
            <label>מייל</label>
            <input type="text" class="form-control" pattern="[a-z0-9._%+-]+@gmail.com" formControlName="email">
            <ng-container *ngIf="registerForm.controls['email'].errors&& !registerForm.controls['email'].pristine">
                <div class="alert alert-danger"> not email valid</div>
            </ng-container>
        </div>
        <div class="form-group">
            <label>סיסמה</label>
            <input type="password" class="form-control"  pattern="(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{6,12}" title="הסיסמא חייב להכיל לפחות אות קטנה אחת, אות גדולה אחת ומספר אחד. 6-12 תווים"formControlName="password">
            <ng-container *ngIf="registerForm.controls['password'].errors&& !registerForm.controls['password'].pristine">
                <div class="alert alert-danger"> not name valid</div>
            </ng-container>
        </div>

        <button class="save" [disabled]="!registerForm.valid" (click)="register()">שלח</button>
    </form>


</div>
